INSERT INTO `bees_job` (`id`, `jobnum`, `jopaddress`, `joblasttime`, `content`, `zwyq`) VALUES
(409, '销售工程师', '全国', '2014-03-01', '<p>\r\n	<strong>岗位职责：</strong>&nbsp;&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、面向全国广播电视、视频投影、安防监控等行业，通过与行业和地区渠道商的开发合作，进行销售拓展，达成暖度销售任务和市场目标；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、建立良好的上下游关系，开发和管理目标客户和市场。<br />\r\n	<strong>岗位要求：</strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、大专以上学历，专业不限（电子相关专业优先）；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、具有强烈的市场开拓精神，敏感的下手意识以及开放的合作态度；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、具有积极乐观向上的性格，高度的上进心和责任心；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;4、善于表达和沟通，良好的人际关系理解能力；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;5、较强的学习和适应能力，工作敬业，能够经常出差。</p>\r\n', '营销中心-销售部'),
(410, '硬件研发工程师', '大连', '2014-03-01', '<p>\r\n	<strong>岗位职责：&nbsp;&nbsp;&nbsp; </strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;新产品的开发及原产品的升级。<br />\r\n	<strong>岗位要求：</strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、本科及以上学历；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、电子信息工程、通讯、自动化、计算机等相关专业；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、熟练掌握以下技术者优先：<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;视音频领域的模拟、数字电路设计；</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;单片机、ARM、FPGA等系统的设计和编程；</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C语言、Verilog、VHDL等编程语言。&nbsp;</p>\r\n', '技术中心-研发三部'),
(411, '产品检测工程师', '大连', '2014-03-01', '<p>\r\n	<strong>岗位职责：</strong>&nbsp;&nbsp;&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;出厂设备的调试、检测工作。<br />\r\n	<strong>岗位要求：</strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、中专及以上学历；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、电子、机械、自动化、计算机等相关专业；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、具有良好的团队意识及沟通能力，较强的动手能力，工作认真负责；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;4、有电子设备调试经验者优先。</p>\r\n', '产品中心-产品二组'),
(412, '科研项目申报专员', '大连', '2014-03-01', '<p>\r\n	<strong>岗位职责：</strong>&nbsp;&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、负责公司科技计划项目及专项资金引进工作；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、熟练掌握项目申报相关工作流程；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、负责公司资质申请与认真等相关工作（申请专利、办理知识产权等）；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;4、于政府部门联络等事务想工作。</p>\r\n<p>\r\n	<strong>岗位要求：</strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、工科本科及以上学历；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、具较强的独自处事能力、对外沟通能力及立项起草书写功底；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、有相关科技项目、政府补贴项目申报工作经验或口才、社交能力强者优先。</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '行管中心-综合部'),
(413, '销售工程师', '全国', '2014-03-01', '<p>\r\n	<strong>岗位职责：</strong>&nbsp;&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、面向全国广播电视、视频投影、安防监控等行业，通过与行业和地区渠道商的开发合作，进行销售拓展，达成暖度销售任务和市场目标；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、建立良好的上下游关系，开发和管理目标客户和市场。<br />\r\n	<strong>岗位要求：</strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、大专以上学历，专业不限（电子相关专业优先）；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、具有强烈的市场开拓精神，敏感的下手意识以及开放的合作态度；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、具有积极乐观向上的性格，高度的上进心和责任心；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;4、善于表达和沟通，良好的人际关系理解能力；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;5、较强的学习和适应能力，工作敬业，能够经常出差。</p>\r\n', '营销中心-销售部'),
(414, '硬件研发工程师助理', '大连', '2014-03-01', '<p>\r\n	<strong>岗位职责：&nbsp;&nbsp;&nbsp; </strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;从事单板、元器件、设备等模块开发、测试等工作。</p>\r\n<p>\r\n	&nbsp;<strong>岗位要求：</strong><br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;1、本科及以上学历；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;2、电子信息工程、通讯、自动化、计算机等相关专业；<br />\r\n	&nbsp; &nbsp; &nbsp; &nbsp;3、熟练掌握以下技术者优先：<br />\r\n	&nbsp;</p>\r\n', '技术中心-研发三部'),
(420, '', '', '', '<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp;为了适应企业的发展需要，切实搞好企业工会工作，按照上级工会的要求，于7月25日建立科迪工会委员会。</p>\r\n', '');
